import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";


const initialState = [
    { id: '0', name: 'Sandra' },
    { id: '1', name: 'Max' },
    { id: '2', name: 'Boris' },
];

const usersSlice = createSlice({
    name: 'users',
    initialState,
    reducers: {}
});

export const selectAllUsers = (state) => state.users;

export default usersSlice.reducer;
